import { Component, OnInit } from '@angular/core';
import {Observable} from 'rxjs';
import {CategorieService} from 'src/app/categorie/categorie.service';

@Component({
  selector: 'app-vezi-categorie',
  templateUrl: './vezi-categorie.component.html',
  styleUrls: ['./vezi-categorie.component.css']
})
export class VeziCategorieComponent implements OnInit {


  categorieList$!:Observable<any[]>;
  constructor(private service:CategorieService) { }

  ngOnInit(): void {
    this.categorieList$ = this.service.getCategorieList();
  }
}
